import pandas as pd
import re

def extract_revenue(text):
    """Extracts revenue in crore or billion (INR or USD) from text."""
    if not isinstance(text, str):
        return None

    # Convert text to lowercase for case-insensitive matching
    text = text.lower()

    # First look for 'crore' amounts:
    crore_match = re.search(r'(\d+(?:\.\d+)?)\s*crore', text)
    if crore_match:
        return float(crore_match.group(1))

    # Now look for billion amounts (USD or unspecified):
    billion_match = re.search(r'~(\d+(?:\.\d+)?)\s*billion', text)
    if billion_match:
        return float(billion_match.group(1)) * 75  # Convert USD to INR (approx. 75 INR/USD)

    return None

revenue_data = []
for item in ipl_annual_revenue_table_T002['summary_bullets']:
    revenue = extract_revenue(item)
    if revenue is not None:
        revenue_data.append(revenue)

if revenue_data:
    average_revenue = pd.Series(revenue_data).mean()
    average_revenue_formatted = f'{average_revenue:,.2f} Crore'
else:
