import os
import json
import datetime

file_list = []

for item in os.listdir('.'):
    item_path = os.path.join('.', item)
    try:
        if os.path.isfile(item_path):
            file_type = 'file'
            file_size = os.path.getsize(item_path)
        elif os.path.isdir(item_path):
            file_type = 'directory'
            file_size = 0  # Size is not relevant for directories
        else:
            file_type = 'unknown'
            file_size = 0

        last_modified = datetime.datetime.fromtimestamp(os.path.getmtime(item_path)).isoformat()

        file_info = {
            'filename': item,
            'file_type': file_type,
            'file_size_bytes': file_size,
            'last_modified': last_modified
        }
        file_list.append(file_info)
    except Exception as e:
        print(f"Error processing {item}: {e}")

output_file = 'file_listing_T001.json'
with open(output_file, 'w') as f:
    json.dump(file_list, f, indent=4)

return {'file_listing_json_T001': output_file}

